import {
    RouterProvider,
    createBrowserRouter,
    createRoutesFromElements,
    Route,
    Link
  } from "react-router-dom";

  import ReactDOM from 'react-dom/client';
  import Error from "../components/Error";
  import Login, { action as loginAction } from "../pages/Login";
  import CashierPOS from '../pages/CashierPOS';
  import CustomerPOS from '../pages/CustomerPOS';
  import DispatchCounter from '../pages/DispatchCounter';
  import KitchenKDS1 from '../pages/KitchenKDS1';
  import KitchenKDS2 from '../pages/KitchenKDS2';
  import KitchenKDS3 from '../pages/KitchenKDS3';
  import Manager360View from '../pages/Manager360View';
  import OrderStatusDiningDisplayTV from '../pages/OrderStatusDiningDisplayTV';
  import OrderStatusKitchenDisplayTV from '../pages/OrderStatusKitchenDisplayTV';

  const router = createBrowserRouter(createRoutesFromElements(
    <Route path="/" element={<Layout />}>
      <Route index element={<Home />} />
      <Route path="about" element={<About />} />
      <Route
        path="login"
        element={<Login />}
        action={loginAction}
      />
      <Route
        path="vans"
        element={<Vans />}
        errorElement={<Error />}
        loader={vansLoader}
      />
      <Route
        path="vans/:id"
        element={<VanDetail />}
        errorElement={<Error />}
        loader={vanDetailLoader}
      />

      <Route element={<AuthRequired />}>
        <Route path="host" element={<HostLayout />} >
          <Route
            index
            element={<Dashboard />}
            errorElement={<Error />}
            loader={dashboardLoader}
          />
          <Route path="/cashier-pos" element={<CashierPOS />}/>
          <Route path="/customer-pos" element={<CustomerPOS />}/>
          <Route path="/dispatch-counter" element={<DispatchCounter />}/>
          <Route path="/kitchen-kds-1" element={<KitchenKDS1 />}/>
          <Route path="/kitchen-kds-2" element={<KitchenKDS2 />}/>
          <Route path="/kitchen-kds-3" element={<KitchenKDS3 />}/>
          <Route path="/manager-360-view" element={<Manager360View />}/>
          <Route path="/order-status-dining-display-tv" element={<OrderStatusDiningDisplayTV />}/>
          <Route path="/order-status-kitchen-display-tv" element={<OrderStatusKitchenDisplayTV />}/>
        </Route>
      </Route>
      <Route path="*" element={<NotFound />} />
    </Route>
  ))

  export default router;
